import React, { useEffect, useState } from 'react';
import Navigation from '../components/Navigation';
import axios from "axios";
import Card from './Card';
import Cookies from 'universal-cookie';

const Edit = () => {
    const [optionValue, setOptionValue] = useState(0);
    let [data, setData] = useState([]);
    data = data.filter((country) => country.capital !== undefined).sort((a, b) => b.population - a.population)
    const cookies = new Cookies();
    const cookiesName = 'villes'
    const [villes, setVilles] = useState([]);

    const SetVille = (ville) => {
        setVilles([...villes, ville]);
        let d = new Date();
        d.setTime(d.getTime() + (30*60*1000));
        cookies.set(cookiesName, villes, { path: '/', secure: true, expires: d});
    }

    const addPays = (i) => {
        const ville = {
            "id": i,
            "flag": data[i].flags.svg,
            "pays": data[i].translations.fra.common,
            "capital": data[i].capital[0],
            "population": data[i].population.toLocaleString(),
            "lat": data[i].latlng[0].toLocaleString(),
            "lng": data[i].latlng[1].toLocaleString()
        }
        if(cookies.get(cookiesName) !== undefined){
            setVilles(cookies.get(cookiesName));
        }
        SetVille(ville)
        console.log(villes);
        console.log(cookies.get(cookiesName));
    }

    useEffect(() => {
        axios
        .get("https://restcountries.com/v3.1/all")
        .then((res) => setData(res.data));
    }, [])

    const PaysListe = () => {
        console.log(cookies.get(cookiesName)["pays"]);
        if(cookies.get(cookiesName) !== undefined){
            return(
                <ul>
                {cookies
                .get(cookiesName)
                    .map((country, index) => (
                        <Card key={index} country={country}/>
                ))}
                </ul>
            )
        }
    }

    return (
        <div>
        <Navigation/>
        <div className='edit'>
            <div className='selector'>
            <h1>Mode Edition : </h1>
            <select onChange={(e) => setOptionValue(parseInt(e.target.value))}>
                {data.filter((country) => !country.translations.fra.common.includes(cookies.get(cookiesName)[0]["pays"]))
                .map((country, index) => (
                <option key={index} value={index}>{country.capital + " (" + country.translations.fra.common + ")"}</option>
                ))};
            </select>
            <button onClick={() => addPays(optionValue)}>Ajouter</button>
            </div>
            <PaysListe/>
        </div>
        </div>
    );
};

export default Edit;
