import React, { useEffect, useState } from 'react';
import Navigation from '../components/Navigation';
import axios from "axios";
import Card from './Card';

const Edit = () => {
    const [optionValue, setOptionValue] = useState("");
    let [data, setData] = useState([]);
    data = data.filter((country) => country.capital !== undefined).sort((a, b) => b.population - a.population)

    useEffect(() => {
        axios
        .get("https://restcountries.com/v3.1/all")
        .then((res) => setData(res.data));
    }, [])

    return (
        <div>
        <Navigation/>
        <div className='edit'>
            <div className='selector'>
            <h1>Mode Edition : </h1>
            <select onChange={(e) => setOptionValue(e.target.value)}>
                {data
                .map((country, index) => (
                <option key={index} value={index}>{country.capital + " (" + country.translations.fra.common + ")"}</option>
                ))};
            </select>
            <button onClick={() => {
                console.log(data[optionValue].capital);
            }
            }>Ajouter</button>
            </div>
            <ul>
                {data
                    .map((country, index) => (
                        <Card key={index} country={country}/>
                ))}
            </ul>
        </div>
        </div>
    );
};

export default Edit;
